import { useState } from "react";
import styled from "styled-components";
import { theme } from "../../../theme/Theme";
import { FunctionComponent } from "react";
import { IStyledProps, IThemeStateProps } from "../../../interfaces/types";

import MainArea from "./MediaArea";
import StripContainer from "./ImageStrip";

const ImageSideContainer = styled.div`
  width: 100%;
  height: 100%;

  padding: ${theme.spacing.space.small};
  border-right: solid 5px ${(props: IStyledProps) => props.themeState.borderColor};

  display: flex;
  flex-direction: column;
`;

interface IImageSideProps {
  data: any;
  themeState: IThemeStateProps;
}

const ImageSide: FunctionComponent<IImageSideProps> = ({ data, themeState }) => {
  const [selectedMedia, setSelectedMedia] = useState<number>(0);
  return (
    <ImageSideContainer themeState={themeState}>
      <MainArea data={data} />
      <StripContainer data={data} />
    </ImageSideContainer>
  );
};

export default ImageSide;
