import styled from "styled-components";
import { theme } from "../../../../theme/Theme";
import { IStyledProps } from "../../../../interfaces/types";
import { IYoutubeStyleProps, IYoutubeEmbed } from "../types/types";

export const PaleText = styled.p`
  flex: 1;
  font-size: ${theme.font.fontsizes.text};
  color: #84847e;
`;

export const Text = styled.p`
  flex: 2;
  font-size: ${theme.font.fontsizes.text};
  color: ${theme.colors.text.primary};
  margin-bottom: ${theme.spacing.space.medium};
`;

export const ProjectContainer = styled.div`
  display: flex;
  overflow: hidden;
`;

export const DescriptionContainer = styled.div`
  width: 100%;
  padding: ${theme.spacing.space.small};
  display: flex;
`;

export const ImageSideContainer = styled.div`
  width: 100%;
  height: 100%;

  padding: ${theme.spacing.space.small};
  border-right: solid 5px ${(props: IStyledProps) => props.themeState.borderColor};

  display: flex;
  flex-direction: column;
`;

export const StripContainer = styled.div`
  width: 100%;
  max-height: 150px;
  flex: 1;

  background-color: pink;

  display: flex;
  justify-content: space-between;
  align-items: center;

  padding: 0 ${theme.spacing.space.large};
`;

export const ThumbNail = styled.img`
  width: 100px;
  height: auto;
`;

export const MediaAreaContainer = styled.div`
  width: 100%;
  flex: 3;

  display: flex;
  justify-content: center;
  align-items: center;

  padding: ${theme.spacing.space.large};
`;

export const TitleContainer = styled.div`
  width: 100%;
  height: max-content;

  padding: ${theme.spacing.space.small};
  display: flex;
  justify-content: space-between;
  align-items: center;
  border-bottom: solid 1px black;
`;

export const TextSideContainer = styled.div`
  width: 100%;
  height: 100%;

  padding: ${theme.spacing.space.small};

  display: flex;
  flex-direction: column;

  overflow-x: hidden;
  overflow-y: scroll;
`;

export const MainTitleContainer = styled.div`
  width: 100%;
  height: max-content;

  padding: ${theme.spacing.space.small};
  display: flex;
  justify-content: space-between;
  align-items: center;

  margin-bottom: ${theme.spacing.space.medium};
`;

export const TitleText = styled.h5`
  font-size: ${theme.font.fontsizes.h5};
  color: ${theme.colors.text.primary};
`;

export const VideoContainer = styled.div`
  width: 100%;
  height: 100%;

  border: solid 5px ${(props: IYoutubeStyleProps) => props.themeState.borderColor};
  border-radius: 15px;

  filter: ${(props: IYoutubeStyleProps) => (!props.clicked ? "grayscale(100%)" : "grayscale(0%)")};
`;

export const IFrameContainer = styled.iframe`
  width: 100%;
  height: 100%;
  border-radius: 10px;
`;
