import React, { FunctionComponent, useState } from "react";
import { Container, EmailSideBar, EmailInbox, EmailContainer, EmailInput, EmailTextBox } from "./styles/styled";
import { IContactPageProps } from "./types/types";

const ContactPage: FunctionComponent<IContactPageProps> = ({ themeState }) => {
  const [userMessage, setUserMessage] = useState({
    to: "To: Russell",
    from: "",
    subject: "",
    message: "",
  });

  const onChangeText = (e: React.ChangeEvent) => {
    const element = e.target as HTMLInputElement;
    const targetID = e.target.id;
    setUserMessage({ ...userMessage, [targetID]: element.value });
    console.log(userMessage);
  };

  return (
    <Container themeState={themeState}>
      <EmailSideBar themeState={themeState} />
      <EmailInbox themeState={themeState} />
      <EmailContainer themeState={themeState}>
        <EmailInput id="to" value={userMessage.to} />
        <EmailInput id="from" value={userMessage.from} placeholder={"From: "} onChange={onChangeText} />
        <EmailInput id="subject" value={userMessage.subject} placeholder={"Add subject"} onChange={onChangeText} />
        <EmailTextBox
          id="message"
          type={"textbox"}
          value={userMessage.message}
          placeholder={"Type your message here.."}
          onChange={onChangeText}
        />
      </EmailContainer>
    </Container>
  );
};

export default ContactPage;
