import { useState, useEffect } from "react";

function getContainerDimensions() {
  const { innerWidth: width, innerHeight: height } = window;

  return {
    width,
    height,
  };
}

export default function useContainerDimensions(div: HTMLDivElement | null) {
  const [containerDimensions, setContainerDimensions] = useState(getContainerDimensions());

  useEffect(() => {
    function handleResize() {
      setContainerDimensions(getContainerDimensions());
    }

    div?.addEventListener("resize", handleResize);
    return () => div?.removeEventListener("resize", handleResize);
  }, [div]);

  return containerDimensions;
}
