import React, { useState, useEffect } from "react";
import styled from "styled-components";
import colorArray from "./colors";
import colorsArray from "./colors";

interface flipProps {
  flip: boolean;
}

const CardContainer = styled.div`
  background-color: transparent;
  position: relative;
  width: 50px;
  height: 50px;

  transition: all 0.2s;
`;

const InnterContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  transition: transform 0.8s;
  transform-style: preserve-3d;

  transform: ${(props: flipProps) => (props.flip ? "rotateX(180deg)" : "none")};
`;

const FrontSide = styled.div`
  background-color: transparent;
  position: absolute;
  width: 100%;
  height: 100%;
  -webkit-backface-visibility: hidden; /* Safari */
  backface-visibility: hidden;
`;

const BackSide = styled.div`
  background-color: orange;
  position: absolute;
  width: 100%;
  height: 100%;
  -webkit-backface-visibility: hidden; /* Safari */
  backface-visibility: hidden;
  transform: rotateX(180deg);
`;

export default function Square() {
  const [flipTime, setFlipTime] = useState(false);
  const [color, setColor] = useState<string>("");

  useEffect(() => {
    const randomNumber = Math.floor(Math.random() * colorsArray.length);
    setColor(colorArray[randomNumber]);

    const randomTime = Math.floor(Math.random() * 100000);
    setTimeout(() => {
      setFlipTime(true);
    }, randomTime);
  }, []);

  return (
    <CardContainer>
      <InnterContainer flip={flipTime}>
        <FrontSide />
        <BackSide />
      </InnterContainer>
    </CardContainer>
  );
}
