import { useContext, useEffect, useRef } from "react";
import ThemeContext from "../../../context/theme/themeContext";
import { FunctionComponent } from "react";
import { IProjectsPageProps } from "../types/interfaces";
import useContainerDimensions from "../../../hooks/useCheckContainerSize";
import { ProjectContainer } from "./styles/styled";
import TextSide from "./TextSide";
import ImageSide from "./ImageSide";

const ProjectPage: FunctionComponent<IProjectsPageProps> = ({ themeState, data }) => {
  const { theme } = useContext(ThemeContext);
  const containerElement = useRef(null) as React.MutableRefObject<HTMLDivElement | null>;
  const { width, height } = useContainerDimensions(containerElement.current);

  console.log(width);

  return (
    <ProjectContainer ref={containerElement} style={{ flexDirection: width < 600 ? "column" : "row" }}>
      <ImageSide data={data} themeState={theme} />
      <TextSide data={data} />
    </ProjectContainer>
  );
};

export default ProjectPage;
