import styled from "styled-components";
import { theme } from "../theme/Theme";
import { IStyledProps } from "../interfaces/types";

interface IAppContainer extends IStyledProps {
  isMobile: boolean;
  mobileHeight: number;
}

export const AppContainer = styled.div`
  position: relative;
  width: 100vw;
  height: ${(props: IAppContainer) => (!props.isMobile ? "100vh" : props.mobileHeight + "px")};
  position: relative;
  overflow: hidden;

  background-color: ${(props: IAppContainer) => props.themeState.backgroundColor};

  /* width */
  *::-webkit-scrollbar {
    width: 30px;
  }

  /* Track */
  *::-webkit-scrollbar-track {
    background: ${(props: IAppContainer) => props.themeState.borderColor};
  }

  /* Handle */
  *::-webkit-scrollbar-thumb {
    background: ${(props: IAppContainer) => props.themeState.terminalColor};
    border-left: ${theme.sizing.borderThickness} solid ${(props) => props.themeState.borderColor};
  }

  /* Handle on hover */
  *::-webkit-scrollbar-thumb:hover {
    /* cursor: pointer;
    filter: brightness(50%); */
  }
`;
