import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { string } from "zod";
import colorArray from "./colors";
import colorsArray from "./colors";

type flipProp = { flip: boolean };
type colorProp = { color: string };
type secondColorolorProp = { secondColor: string };

const CardContainer = styled.div`
  background-color: transparent;
  position: relative;
  width: 100px;
  height: 100px;

  transition: all 0.2s;
`;

const InnterContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  transition: transform 0.8s;
  transform-style: preserve-3d;

  transform: ${(props: flipProp) => (props.flip ? "rotateX(180deg)" : "rotateX(359deg)")};
`;

const FrontSide = styled.div`
  background-color: ${(props: secondColorolorProp) => props.secondColor};
  position: absolute;
  width: 100%;
  height: 100%;
  -webkit-backface-visibility: hidden; /* Safari */
  backface-visibility: hidden;
`;

const BackSide = styled.div`
  background-color: ${(props: colorProp) => props.color};
  position: absolute;
  width: 100%;
  height: 100%;
  -webkit-backface-visibility: hidden; /* Safari */
  backface-visibility: hidden;
  transform: rotateX(180deg);
`;

export default function Square() {
  const [flipTime, setFlipTime] = useState(false);
  const [currentColor, setCurrentColor] = useState<string>("");
  const [secondColor, setSecondColor] = useState<string>("transparent");

  useEffect(() => {
    const randomTime = Math.floor(Math.random() * 100000) + 5000;
    setInterval(() => {
      const randomNumber = Math.floor(Math.random() * colorsArray.length);
      const currCol = currentColor;

      setSecondColor(currCol);

      setTimeout(() => {
        setCurrentColor(colorArray[randomNumber]);
        setFlipTime(!flipTime);
      }, 500);
    }, randomTime);
  }, []);

  return (
    <CardContainer>
      <InnterContainer flip={flipTime}>
        <FrontSide secondColor={secondColor} />
        <BackSide color={currentColor} />
      </InnterContainer>
    </CardContainer>
  );
}
