import styled from "styled-components";
import { theme } from "../../../theme/Theme";
import { IStyledProps } from "../../../interfaces/types";

export const MainDivStyle = styled.div`
  border-radius: 15px;
  background-color: ${(props: IStyledProps) => props.themeState.terminalColor};
  border: solid 5px ${(props: IStyledProps) => props.themeState.borderColor};
  box-shadow: 15px 15px 0 ${(props: IStyledProps) => props.themeState.shadowColor};
`;

export const PageContainer = styled.main`
  width: 100vw;
  height: 100vh;

  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

export const UserIcon = styled(MainDivStyle)`
  width: 100px;
  height: 100px;
  border-radius: 50%;

  margin-bottom: ${theme.spacing.space.large};
`;

export const InputField = styled(MainDivStyle)`
  width: 300px;
  height: 50px;
`;

export const HelpBox = styled(MainDivStyle)`
  width: 35px;
  height: 35px;
  border-radius: 15px;
`;

export const LoginButton = styled(MainDivStyle)`
  width: 170px;
  height: 50px;
`;
