import { useContext, useState } from "react";
import { AppContainer } from "./styles/styled";
import ThemeContext from "./context/theme/themeContext";
import Icon from "./components/Icons/Index";
import BottomBar from "./components/BottomBar/Index";
import Desktop from "./components/Pages/Desktop";
// import WindowContext from "../context/window/WindowContext";

interface IWindowObject {
  id: number;
  label: string;
  pageName: string;
  windowType: string;
  isProject: boolean;
}

export default function Home() {
  const [selectedIcon, setSelectedIcon] = useState<string | null>(null);
  const [windowList, setWindowList] = useState<Array<IWindowObject>>([]);
  const themeContext = useContext(ThemeContext);
  const { theme } = themeContext;
  // const windowContext = useContext(WindowContext);
  // const { windowState } = windowContext;

  const createWindow = (pageName: string, label: string, windowType: string, isProject: boolean) => {
    const newWindow: IWindowObject = {
      id: Math.random() * 10000000,
      label,
      pageName,
      windowType,
      isProject,
    };

    setWindowList([...windowList, newWindow]);
  };

  return (
    <AppContainer themeState={theme}>
      <Desktop themeState={theme}>
        <Icon
          themeState={theme}
          pageName={"About"}
          label={"about"}
          aniDelay={0.1}
          isWeb={null}
          selectedIcon={selectedIcon}
          setSelectedIcon={setSelectedIcon}
          createWindow={createWindow}
        />
      </Desktop>
      <BottomBar />
    </AppContainer>
  );
}
