import { FunctionComponent, useContext, useState } from "react";
import ThemeContext from "../../context/theme/themeContext";
import styled from "styled-components";

import { CanvasPath, ReactSketchCanvas } from "react-sketch-canvas";

const Container = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
`;

interface ICanvasProps {
  canvasRef: React.MutableRefObject<any>;
}

const Index: FunctionComponent<ICanvasProps> = ({ canvasRef }) => {
  const { theme } = useContext(ThemeContext);
  const [canDraw, setCanDraw] = useState(false);

  const onDraw = async (e: CanvasPath) => {
    setCanDraw(true);
    const time = await canvasRef.current.getSketchingTime();

    if (time > 6000) {
      console.log(time);
      console.log("WOuld save drawing");
      const baseImage = await canvasRef.current.exportImage();
    }
  };

  return (
    <Container>
      <ReactSketchCanvas
        ref={canvasRef}
        canvasColor="transparent"
        width="100vw"
        height="100vh"
        strokeWidth={canDraw ? 3 : 0}
        strokeColor={theme.borderColor}
        onStroke={(e) => onDraw(e)}
        withTimestamp={true}
      />
    </Container>
  );
};

export default Index;
