import { useContext } from "react";
import styled from "styled-components";
import { theme } from "../../theme/Theme";
import { IStyledProps } from "../../interfaces/types";
import ThemeContext from "../../context/theme/themeContext";
import Loading from "./Loading";

export const MainDivStyle = styled.div`
  border-radius: 15px;
  background-color: ${(props: IStyledProps) => props.themeState.terminalColor};
  border: solid 5px ${(props: IStyledProps) => props.themeState.borderColor};
  box-shadow: 5px 6px 0 ${(props: IStyledProps) => props.themeState.shadowColor};
`;

export const PageContainer = styled.main`
  width: 100vw;
  height: 100vh;

  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

export const MessageContainer = styled(MainDivStyle)`
  position: relative;
  width: 50%;
  height: max-content;
  border-radius: 15px;
  padding: ${theme.spacing.space.medium};

  display: flex;
  align-items: center;
  justify-content: center;

  margin-bottom: ${theme.spacing.space.large};
`;

export default function LoginAbout() {
  const { theme } = useContext(ThemeContext);

  return (
    <PageContainer>
      <MessageContainer themeState={theme}>
        <p>
          Now loading the account of Russell Carey. A passionate coding obsessed English-man currently living in Japan.
          Russell creates interesting, creative and out of the box full stack projects from design to completetion.
        </p>
        <Loading />
      </MessageContainer>
    </PageContainer>
  );
}
