import { useState } from "react";
import styled from "styled-components";
import Square from "./Square";

const Container = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 120vw;
  min-height: 100vh;
  overflow-y: hidden;

  display: flex;
  flex-wrap: wrap;
`;

export default function Flip() {
  const { innerWidth, innerHeight } = window;
  const numberOfItemsX = Math.ceil(innerWidth / 50);
  const numberOfItemsY = Math.ceil(innerHeight / 50);
  const total = Math.ceil(numberOfItemsX * numberOfItemsY);
  console.log(numberOfItemsX, numberOfItemsY, total);

  return (
    <Container>
      {[
        ...Array(total).map((squares) => {
          console.log("mad e a square");
          return <Square />;
        }),
      ]}
    </Container>
  );
}
