import { useState, useContext, useEffect } from "react";
import styled from "styled-components";
import { theme } from "./theme/Theme";
import ThemeContext from "./context/theme/themeContext";
import Icon from "./components/Icons/Index";
import BottomBar from "./components/BottomBar/Index";
import Desktop from "./components/Pages/Desktop";
// import WindowContext from "../context/window/WindowContext";
import StartMenu from "./components/BottomBar/StartMenu";
import { IStyledProps } from "./interfaces/styled";

const AppContainer = styled.div`
  position: relative;
  width: 100vw;
  height: 100vh;
  position: relative;
  overflow: hidden;

  /* width */
  *::-webkit-scrollbar {
    width: 30px;
  }

  /* Track */
  *::-webkit-scrollbar-track {
    background: ${(props: IStyledProps) => props.themeState.borderColor};
  }

  /* Handle */
  *::-webkit-scrollbar-thumb {
    background: ${(props) => props.themeState.backgroundColor};
  }

  /* Handle on hover */
  *::-webkit-scrollbar-thumb:hover {
    background: #555;
  }
`;

export default function Home({ data }) {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [showStartMenu, setShowStartMenu] = useState(false);
  const themeContext = useContext(ThemeContext);
  const { theme } = themeContext;
  // const windowContext = useContext(WindowContext);
  // const { windowState } = windowContext;

  // USE EFFECT had it so all icons were unclicked.

  // const getGitData = async () => {
  //   const api = await axios("https://api.github.com/users/RussellCarey/repos");
  //   console.log(api);
  // };

  const toggleStartMenu = () => {
    console.log("Toggling start menu");
    setShowStartMenu(!showStartMenu);
  };

  return (
    <AppContainer themeState={theme}>
      {/* <Head>
        <title>Create Next App</title>
        <meta name="description" content="Generated by create next app" />
        <link rel="icon" href="/favicon.ico" />
      </Head> */}

      <Desktop themeState={theme}>
        {/* Possible img: twitter, git, about, blog */}
        {/* {showStartMenu && <StartMenu themeState={themeState} />} */}
        <Icon themeState={theme} pageName={"About"} label={"about"} img={"about"} aniDelay={0.1} />
        <Icon
          themeState={theme}
          pageName={"Projects"}
          label={"projects"}
          img={"projects"}
          windowType={"sidebar"}
          aniDelay={0.5}
          data={data}
        />
        <Icon themeState={theme} pageName={"Blog"} label={"blog"} img={"blog"} aniDelay={0.8} />
        <Icon themeState={theme} pageName={"Contact"} label={"contact"} img={"contact"} aniDelay={1} />
        <Icon
          themeState={theme}
          pageName={"Twitter"}
          label={"twitter"}
          img={"twitter"}
          isWeb="http://twitter.com/russellcareyy"
          aniDelay={1.2}
        />
        <Icon
          themeState={theme}
          pageName={"github"}
          label={"github"}
          img={"git"}
          isWeb="http://github.com/RussellCarey"
          aniDelay={1.3}
        />
        <Icon themeState={theme} pageName={"Howto"} label={"info"} img={"info"} aniDelay={1.7} windowType={"sidebar"} />

        {/* {windowState.windows.length > 0
          ? windowState.windows.map((window) => {
              return window;
            })
          : null} */}
      </Desktop>
      <BottomBar toggleStartMenu={toggleStartMenu} showStartMenu={showStartMenu} />
    </AppContainer>
  );
}
