import {
  SET_DARK_THEME,
  SET_LIGHT_THEME,
  SET_BLUE_THEME,
  SET_PEACH_THEME,
  SET_PINK_THEME,
  SET_PURPLE_THEME,
  SET_TURQUOISE_THEME,
  IThemeState,
} from "./types";

const Reducer = (state: IThemeState, action: any) => {
  switch (action.type) {
    case SET_DARK_THEME:
      return action.payload;

    case SET_LIGHT_THEME:
      return action.payload;

    default:
      return state;
  }
};

export default Reducer;
