import React, { FunctionComponent, useContext } from "react";
import { PopupContainer, Popup, Button } from "./styled/styled";
import ThemeContext from "../../context/theme/themeContext";

interface IPropsPopup {
  message: string[];
  setShowPopup: React.Dispatch<React.SetStateAction<boolean>>;
}

const PopupComp: FunctionComponent<IPropsPopup> = ({ message, setShowPopup }) => {
  const { theme } = useContext(ThemeContext);

  return (
    <PopupContainer>
      <Popup themeState={theme}>
        {message.map((mess) => {
          return <p style={{ marginBottom: "5px" }}>{mess}</p>;
        })}
        <Button onClick={() => setShowPopup(false)}>close</Button>
      </Popup>
    </PopupContainer>
  );
};

export default PopupComp;
