import { useState, useContext, useEffect } from "react";
import styled from "styled-components";
import { theme } from "./theme/Theme";
import axios from "axios";
import Icon from "./components/Icons/icon";
import BottomBar from "./components/BottomBar/Index";
import Desktop from "./components/Pages/Desktop";
// import WindowContext from "../context/window/WindowContext";
import StartMenu from "./components/BottomBar/StartMenu";

const AppContainer = styled.div`
  position: relative;
  width: 100vw;
  height: 100vh;
  position: relative;
  overflow: hidden;

  /* width */
  *::-webkit-scrollbar {
    width: 30px;
  }

  /* Track */
  *::-webkit-scrollbar-track {
    background: ${(props) => props.themeState.borderColor};
  }

  /* Handle */
  *::-webkit-scrollbar-thumb {
    background: ${(props) => props.themeState.backgroundColor};
    /* border-left: ${theme.Sizing.borderThickness} solid ${(props) => props.themeState.borderColor}; */
  }

  /* Handle on hover */
  *::-webkit-scrollbar-thumb:hover {
    background: #555;
  }
`;

export default function Home({ data }) {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [showStartMenu, setShowStartMenu] = useState(false);
  const windowContext = useContext(WindowContext);
  const { windowState } = windowContext;

  const [themeState, setThemeState] = useState({
    wallpaperColor: isDarkMode ? theme.Colors.ui.wallpaperDark : theme.Colors.ui.wallpaperColor,
    borderColor: isDarkMode ? theme.Colors.ui.borderDarkmode : theme.Colors.ui.borderColor,
    backgroundColor: isDarkMode ? theme.Colors.ui.backgroundDarkmode : theme.Colors.ui.backgroundColor,
    fontColor: isDarkMode ? theme.Colors.text.primaryDarkmode : theme.Colors.text.primary,
  });

  useEffect(() => {
    // Disable right click menu
    // document.addEventListener("contextmenu", (event) => event.preventDefault());

    console.log(data);

    // Any icon that is clicked away from or on a new icon gets the highlight removed
    const iconClick = window.addEventListener("click", (e) => {
      const iconParent = e.target.closest(".windowIcon");

      const allIcons = document.querySelectorAll(".windowIcon");

      allIcons.forEach((icon) => {
        icon.childNodes[1].className == "iconText";
        icon.childNodes[1].style.backgroundColor = "transparent";
      });

      if (!iconParent) return;

      // Set the background of clicked icon if there is one.
      iconParent.childNodes[1].style.backgroundColor = "#30312c23";

      // Set start menu away
    });

    // getGitData();
  }, []);

  // const getGitData = async () => {
  //   const api = await axios("https://api.github.com/users/RussellCarey/repos");
  //   console.log(api);
  // };

  const toggleStartMenu = () => {
    console.log("Toggling start menu");
    setShowStartMenu(!showStartMenu);
  };

  return (
    <AppContainer themeState={themeState}>
      {/* <Head>
        <title>Create Next App</title>
        <meta name="description" content="Generated by create next app" />
        <link rel="icon" href="/favicon.ico" />
      </Head> */}

      <Desktop themeState={themeState}>
        {/* Possible img: twitter, git, about, blog */}
        {showStartMenu && <StartMenu themeState={themeState} />}
        <Icon themeState={themeState} pageName={"About"} label={"about"} img={"about"} aniDelay={0.1} />
        <Icon
          themeState={themeState}
          pageName={"Projects"}
          label={"projects"}
          img={"projects"}
          windowType={"sidebar"}
          aniDelay={0.5}
          data={data}
        />
        <Icon themeState={themeState} pageName={"Blog"} label={"blog"} img={"blog"} aniDelay={0.8} />
        <Icon themeState={themeState} pageName={"Contact"} label={"contact"} img={"contact"} aniDelay={1} />
        <Icon
          themeState={themeState}
          pageName={"Twitter"}
          label={"twitter"}
          img={"twitter"}
          isWeb="http://twitter.com/russellcareyy"
          aniDelay={1.2}
        />
        <Icon
          themeState={themeState}
          pageName={"github"}
          label={"github"}
          img={"git"}
          isWeb="http://github.com/RussellCarey"
          aniDelay={1.3}
        />
        <Icon
          themeState={themeState}
          pageName={"Howto"}
          label={"info"}
          img={"info"}
          aniDelay={1.7}
          windowType={"sidebar"}
        />

        {windowState.windows.length > 0
          ? windowState.windows.map((window) => {
              return window;
            })
          : null}
      </Desktop>
      <BottomBar themeState={themeState} toggleStartMenu={toggleStartMenu} showStartMenu={showStartMenu} />
    </AppContainer>
  );
}
