  width: 300px;
  height: 100%;
  border-right: solid 5px black;

  display: flex;
  flex-direction: column;
`;

const BlogSidebar = () => {
  const { theme } = useContext(ThemeContext);

  return <SideBar></SideBar>;
};

export default BlogSidebar;
