import React, { useEffect, useContext, useState, FunctionComponent } from "react";
import ThemeContext from "../../context/theme/themeContext";
import {
  PageContainer,
  UserIconContainer,
  UserIcon,
  InputField,
  FakeInputField,
  HelpBox,
  LoginButton,
  AnimatedLogin,
} from "./styles/styled";

import Loading from "../InnerPages/Loading";

interface ILoginPage {
  setIsLoggedIn: React.Dispatch<React.SetStateAction<boolean>>;
}

const LoginPage: FunctionComponent<ILoginPage> = ({ setIsLoggedIn }) => {
  const { theme } = useContext(ThemeContext);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [name, setName] = useState<string>("Russell Carey");
  const [password, setPassword] = useState<string>("******************");

  return (
    <PageContainer>
      {!isLoading ? (
        <>
          <UserIconContainer themeState={theme}>
            <UserIcon />
          </UserIconContainer>
          <FakeInputField themeState={theme}>
            <InputField themeState={theme} type="text" value={name} />
          </FakeInputField>
          <FakeInputField themeState={theme}>
            <InputField themeState={theme} type="text" value={password} />
            <HelpBox themeState={theme}>?</HelpBox>
          </FakeInputField>
          <AnimatedLogin themeState={theme} onClick={() => setIsLoggedIn(true)}>
            login
          </AnimatedLogin>
        </>
      ) : (
        <Loading />
      )}
    </PageContainer>
  );
};

export default LoginPage;
