const endpoint = prismic.getEndpoint("mooblyportfolio");
const accessToken = process.env.PRISMIC_; // Set an access token

const client = prismic.createClient(endpoint, { accessToken });

export const getAllProjects = async () => {
  try {
    const prismicDoc = await client.getAllByType("Projects");
    return prismicDoc;
  } catch (error: any) {
    console.log(error.response);
  }
};
