import React, { FunctionComponent, useState } from "react";
import styled from "styled-components";
import PropTypes from "prop-types";
import { IStyledProps, IThemeStateProps } from "../../../interfaces/types";

interface IYoutubeEmbed {
  id: string;
  themeState: IThemeStateProps;
}

interface IYoutubeStyleProps extends IStyledProps {
  clicked: any;
}

const VideoContainer = styled.div`
  width: 100%;
  height: 100%;

  border: solid 5px ${(props: IYoutubeStyleProps) => props.themeState.borderColor};
  border-radius: 15px;

  filter: ${(props: IYoutubeStyleProps) => (!props.clicked ? "grayscale(100%)" : "grayscale(0%)")};
`;

const IFrameContainer = styled.iframe`
  width: 100%;
  height: 100%;
`;

const YoutubeEmbed: FunctionComponent<IYoutubeEmbed> = ({ id, themeState }) => {
  const [clicked, setClicked] = useState<boolean>(false);

  return (
    <VideoContainer themeState={themeState} clicked={clicked}>
      <IFrameContainer
        src={`https://www.youtube.com/embed/${id}`}
        frameBorder="0"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowFullScreen
        title="Embedded youtube"
        onPlay={() => setClicked(!clicked)}
      />
    </VideoContainer>
  );
};

YoutubeEmbed.propTypes = {
  id: PropTypes.string.isRequired,
};

export default YoutubeEmbed;
