import { FunctionComponent, useContext, useState, useEffect } from "react";
import ThemeContext from "../../context/theme/themeContext";
import styled from "styled-components";
import { CanvasPath, ReactSketchCanvas } from "react-sketch-canvas";

import { getDrawingData, uploadDrawing } from "./services/dbService";

const Container = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
`;

interface ICanvasProps {
  canvasRef: React.MutableRefObject<any>;
}

const Index: FunctionComponent<ICanvasProps> = ({ canvasRef }) => {
  const { theme } = useContext(ThemeContext);
  const [canDraw, setCanDraw] = useState(false);
  const [gotInitial, setGotInitial] = useState(false);
  const [allSet, setAllSet] = useState(false);

  // Get current drawing state from the DB..
  const getInitialDrawing = async () => {
    const drawingData = await getDrawingData();
    if (drawingData.data.status === "success") setGotInitial(true);
    const paths = await JSON.parse(drawingData.data.data.rows[0].data);
    await canvasRef.current.loadPaths(paths);
  };

  useEffect(() => {
    getInitialDrawing();
  }, []);

  const onDraw = async (e: CanvasPath) => {
    // On clicking the BG show the drawing mode..
    const time = await canvasRef.current.getSketchingTime();
    if (!canDraw) setCanDraw(true);

    // If the user has been drawing for over 6 seconds then upload their contribution to the db on each stroke ?
    if (time > 6000 && gotInitial) {
      const drawingPaths = await canvasRef.current.exportPaths();
      const drawingString = await JSON.stringify(drawingPaths);
      await uploadDrawing(drawingString);
    }
  };

  return (
    <Container>
      <ReactSketchCanvas
        ref={canvasRef}
        canvasColor="transparent"
        width="0"
        height="0"
        strokeWidth={canDraw ? 3 : 0}
        strokeColor={theme.borderColor}
        onStroke={(e) => onDraw(e)}
        withTimestamp={true}
      />
    </Container>
  );
};

export default Index;
