import React, { useEffect, useContext, useState, FunctionComponent, useRef } from "react";
import ThemeContext from "../../context/theme/themeContext";
import {
  PageContainer,
  UserIconContainer,
  UserIcon,
  InputField,
  FakeInputField,
  HelpBox,
  LoginButton,
  AnimatedLogin,
} from "./styles/styled";
import { delayTime } from "./utils/typing";
import Loading from "../InnerPages/Loading";

interface ILoginPage {
  setIsLoggedIn: React.Dispatch<React.SetStateAction<boolean>>;
}

const LoginPage: FunctionComponent<ILoginPage> = ({ setIsLoggedIn }) => {
  const { theme } = useContext(ThemeContext);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const nameRef = useRef<string>("");
  const passwordRef = useRef<string>("");

  const populateFieldTyping = async (text: string) => {
    // Set blinking? For the text line?

    // set type speed to a state (random?)

    const textArray = text.split("");

    textArray.forEach(async (letter) => {
      await delayTime(1000);
      console.log(letter);
      nameRef.current = nameRef.current + letter;
    });
  };

  useEffect(() => {
    populateFieldTyping("Russell Carey");
  }, []);

  return (
    <PageContainer>
      {!isLoading ? (
        <>
          <UserIconContainer themeState={theme}>
            <UserIcon />
          </UserIconContainer>
          <FakeInputField themeState={theme}>
            <InputField themeState={theme} type="text" value={nameRef.current} />
          </FakeInputField>
          <FakeInputField themeState={theme}>
            <InputField themeState={theme} type="text" value={passwordRef.current} />
            <HelpBox themeState={theme}>?</HelpBox>
          </FakeInputField>
          <AnimatedLogin themeState={theme} onClick={() => setIsLoggedIn(true)}>
            login
          </AnimatedLogin>
        </>
      ) : (
        <Loading />
      )}
    </PageContainer>
  );
};

export default LoginPage;
