import React, { FunctionComponent } from "react";
import { ControlContainer } from "./styled/styled";
import { uploadDrawing } from "./services/dbService";

interface IPropsCanvasController {
  canvasRef: React.MutableRefObject<any>;
}

const CanvasControls: FunctionComponent<IPropsCanvasController> = ({ canvasRef }) => {
  const saveDrawingToDB = async () => {
    const drawingPaths = await canvasRef.current.exportPaths();
    const drawingString = await JSON.stringify(drawingPaths);
    await uploadDrawing(drawingString);
  };

  return (
    <ControlContainer>
      <button>ERASE</button>
      <button>SAVE</button>
      <button>CLOSE</button>
    </ControlContainer>
  );
};

export default CanvasControls;
