import React, { useEffect, useContext, useState } from "react";
import ThemeContext from "../../context/theme/themeContext";
import {
  PageContainer,
  UserIconContainer,
  UserIcon,
  InputField,
  FakeInputField,
  HelpBox,
  LoginButton,
} from "./styles/styled";

export default function LoginPage() {
  const { theme } = useContext(ThemeContext);
  const [name, setName] = useState<string>("");
  const [password, setPassword] = useState<string>("");

  const typeOutText = async (target: string, setFunction: React.Dispatch<React.SetStateAction<string>>) => {
    const stringArray = target.split("");

    stringArray.forEach((letter) => {
      const timeWait = Math.random() * 2;

      window.setTimeout(() => {
        setFunction(name + letter);
      }, timeWait);
    });
  };

  useEffect(() => {
    typeOutText("Russell Carey", setName);
  }, []);

  return (
    <PageContainer>
      <UserIconContainer themeState={theme}>
        <UserIcon />
      </UserIconContainer>
      <FakeInputField themeState={theme}>
        <InputField themeState={theme} type="text" value={name} />
      </FakeInputField>
      <FakeInputField themeState={theme}>
        <InputField themeState={theme} type="text" value={password} />
        <HelpBox themeState={theme}>?</HelpBox>
      </FakeInputField>
      <LoginButton themeState={theme}>login</LoginButton>
    </PageContainer>
  );
}
