import React, { FunctionComponent } from "react";
import styled from "styled-components";
import { IThemeStateProps } from "../../../interfaces/types";
import { theme } from "../../../theme/Theme";
import YoutubeEmbed from "./Youtube";

const MediaAreaContainer = styled.div`
  width: 100%;
  flex: 3;

  display: flex;
  justify-content: center;
  align-items: center;

  padding: ${theme.spacing.space.xlarge};
`;

const MainImage = styled.img`
  width: 100%;
`;

interface IMediaAreaProps {
  data: any;
  themeState: IThemeStateProps;
}

const MediaArea: FunctionComponent<IMediaAreaProps> = ({ data, themeState }) => {
  return (
    <MediaAreaContainer>
      <YoutubeEmbed id={data.data.youtube} themeState={themeState} />
    </MediaAreaContainer>
  );
};

export default MediaArea;
